import React, { useState } from "react";

//const pokemonName = "Butterfree";
const pokemonName = ["Butterfree", "Ditto", "Spearow", "Emolga", "Komala"];
const CaughtPokemon = (props) => {
  //const [caught, setCaught] = useState(0);
  const [caught, setCaught] = useState([]);
  const [pokemonNameInput, setpokemonNameInput] = useState("");

   console.log(caught);

  function incrementCaught() {
    console.log("click");
    //   setCaught(caught.concat([pokemonName]));
    setCaught(
      caught.concat(pokemonName[Math.floor(Math.random() * pokemonName.length)])
    );
  }
  function handleInputChange(event) {
    setpokemonNameInput(event.target.value);
    console.log(event.target.value);
  };

  return (
    <div>
      <p>
        Caught {caught.length} Pokemon on {props.date}
      </p>
      <input
        type="text"
        name="email"
        placeholder="Email"
        value={pokemonNameInput}
        onChange={handleInputChange}
      />

      <button onClick={incrementCaught}>Click me</button>
      <p>You clicked times {caught.length}</p>
      <ul>
        {caught.map((item) => {
          return <li>{item}</li>;
        })}
      </ul>
    </div>
  );
};
export default CaughtPokemon;
